contracts/AtomicSwap.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IBinanceProxyRouter.sol";
import "./interfaces/IPancakeSwapV2Router.sol";

/**
 * @title AtomicSwap
 * @dev 实现原子化双向 Swap 交易的智能合约
 */
contract AtomicSwap is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // 常量定义
    uint256 private constant DEADLINE_DURATION = 20 minutes;
    address public immutable BINANCE_PROXY_ROUTER;
    address public immutable PANCAKESWAP_ROUTER;

    // 事件定义
    event SwapExecuted(
        address indexed tokenA,
        address indexed tokenB,
        uint256 amountAIn,
        uint256 amountBOut,
        uint256 amountAOut
    );
    event EmergencyWithdraw(address indexed token, uint256 amount);

    /**
     * @dev 构造函数
     * @param _binanceProxyRouter Binance DEX 代理路由器地址
     * @param _pancakeSwapRouter PancakeSwap V2 路由器地址
     */
    constructor(
        address _binanceProxyRouter,
        address _pancakeSwapRouter
    ) Ownable() {
        require(_binanceProxyRouter != address(0), "Invalid proxy router address");
        require(_pancakeSwapRouter != address(0), "Invalid pancake router address");

        BINANCE_PROXY_ROUTER = _binanceProxyRouter;
        PANCAKESWAP_ROUTER = _pancakeSwapRouter;
    }

    /**
     * @dev 执行原子化双向 Swap
     * @param tokenA 源代币地址
     * @param tokenB 中间代币地址
     * @param amountAIn 输入的 TokenA 数量
     * @param amountBMinOut 第一次 Swap 最小返回的 TokenB 数量
     * @param amountAFinalMinOut 第二次 Swap 最小返回的 TokenA 数量
     */
    function executeAtomicSwapPair(
        address tokenA,
        address tokenB,
        uint256 amountAIn,
        uint256 amountBMinOut,
        uint256 amountAFinalMinOut
    ) external nonReentrant {
        require(tokenA != address(0) && tokenB != address(0), "Invalid token address");
        require(amountAIn > 0, "Invalid input amount");
        require(amountBMinOut > 0 && amountAFinalMinOut > 0, "Invalid min output amount");

        // 转移用户的 TokenA 到合约
        IERC20(tokenA).safeTransferFrom(msg.sender, address(this), amountAIn);

        // 授权代理路由器使用 TokenA
        IERC20(tokenA).safeApprove(BINANCE_PROXY_ROUTER, amountAIn);

        // 构造第一次 Swap 的调用数据
        bytes memory firstSwapData = abi.encodeWithSelector(
            IPancakeSwapV2Router.swapExactTokensForTokens.selector,
            amountAIn,
            amountBMinOut,
            _getPath(tokenA, tokenB),
            address(this),
            block.timestamp + DEADLINE_DURATION
        );

        // 执行第一次 Swap (A -> B)
        IBinanceProxyRouter(BINANCE_PROXY_ROUTER).proxySwapV2(
            PANCAKESWAP_ROUTER,
            uint256(uint160(tokenA)),
            amountAIn,
            uint256(uint160(tokenB)),
            amountBMinOut,
            firstSwapData
        );

        // 获取获得的 TokenB 数量
        uint256 tokenBBalance = IERC20(tokenB).balanceOf(address(this));
        require(tokenBBalance >= amountBMinOut, "Insufficient token B received");

        // 授权代理路由器使用 TokenB
        IERC20(tokenB).safeApprove(BINANCE_PROXY_ROUTER, tokenBBalance);

        // 构造第二次 Swap 的调用数据
        bytes memory secondSwapData = abi.encodeWithSelector(
            IPancakeSwapV2Router.swapExactTokensForTokens.selector,
            tokenBBalance,
            amountAFinalMinOut,
            _getPath(tokenB, tokenA),
            address(this),
            block.timestamp + DEADLINE_DURATION
        );

        // 执行第二次 Swap (B -> A)
        IBinanceProxyRouter(BINANCE_PROXY_ROUTER).proxySwapV2(
            PANCAKESWAP_ROUTER,
            uint256(uint160(tokenB)),
            tokenBBalance,
            uint256(uint160(tokenA)),
            amountAFinalMinOut,
            secondSwapData
        );

        // 获取最终的 TokenA 数量
        uint256 finalTokenABalance = IERC20(tokenA).balanceOf(address(this));
        require(finalTokenABalance >= amountAFinalMinOut, "Insufficient final token A received");

        // 将最终的 TokenA 发送给用户
        IERC20(tokenA).safeTransfer(msg.sender, finalTokenABalance);

        emit SwapExecuted(
            tokenA,
            tokenB,
            amountAIn,
            tokenBBalance,
            finalTokenABalance
        );
    }

    /**
     * @dev 紧急提取代币
     * @param token 要提取的代币地址
     * @param amount 要提取的数量
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        require(token != address(0), "Invalid token address");
        require(amount > 0, "Invalid amount");

        IERC20(token).safeTransfer(owner(), amount);
        emit EmergencyWithdraw(token, amount);
    }

    /**
     * @dev 获取交易路径
     * @param tokenIn 输入代币
     * @param tokenOut 输出代币
     * @return 交易路径数组
     */
    function _getPath(address tokenIn, address tokenOut) private pure returns (address[] memory) {
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;
        return path;
    }
}

contracts/interfaces/IBinanceProxyRouter.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IBinanceProxyRouter
 * @dev Binance DEX 代理路由器接口
 */
interface IBinanceProxyRouter {
    /**
     * @dev 代理执行 Swap 操作
     * @param router 底层路由器地址
     * @param fromTokenWithFee 源代币地址
     * @param fromAmt 源代币数量
     * @param toTokenWithFee 目标代币地址
     * @param minReturnAmt 最小返回数量
     * @param callData 底层 Swap 调用数据
     */
    function proxySwapV2(
        address router,
        uint256 fromTokenWithFee,
        uint256 fromAmt,
        uint256 toTokenWithFee,
        uint256 minReturnAmt,
        bytes calldata callData
    ) external payable;

    /**
     * @dev 获取允许的路由器列表
     * @return 允许的路由器地址数组
     */
    function getRouterList() external view returns (address[] memory);

    /**
     * @dev 添加允许的路由器
     * @param router 要添加的路由器地址数组
     */
    function addRouters(address[] calldata router) external;

    /**
     * @dev 移除允许的路由器
     * @param router 要移除的路由器地址数组
     */
    function removeRouters(address[] calldata router) external;

    // 事件定义
    event RouterAllowed(address indexed operator, address[] router);
    event RouterDenied(address indexed operator, address[] router);
    event FeeCollected(address indexed token, address recipient, uint256 amount);

    // 错误定义
    error AddressEmptyCode(address target);
    error AddressInsufficientBalance(address account);
    error ApproveFailed();
    error FailedInnerCall();
    error InvalidFeeValue(uint256 fee);
    error InvalidRouter(address router);
    error MinReturnNotReached(uint256 minReturnAmt, uint256 result);
    error OnlyContractOwner();
    error SafeERC20FailedOperation(address token);
}

contracts/interfaces/IPancakeSwapV2Route.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IPancakeSwapV2Router {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    function swapExactETHForTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable returns (uint[] memory amounts);

    function swapTokensForExactETH(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    function swapExactTokensForETH(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    function swapETHForExactTokens(
        uint amountOut,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable returns (uint[] memory amounts);

    function quote(
        uint amountA,
        uint reserveA,
        uint reserveB
    ) external pure returns (uint amountB);

    function getAmountOut(
        uint amountIn,
        uint reserveIn,
        uint reserveOut
    ) external pure returns (uint amountOut);

    function getAmountIn(
        uint amountOut,
        uint reserveIn,
        uint reserveOut
    ) external pure returns (uint amountIn);

    function getAmountsOut(
        uint amountIn,
        address[] calldata path
    ) external view returns (uint[] memory amounts);

    function getAmountsIn(
        uint amountOut,
        address[] calldata path
    ) external view returns (uint[] memory amounts);
}

